package com.piyush.tick.activity;

import android.app.Activity;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.util.Base64;
import android.widget.BaseAdapter;
import android.widget.ImageView;
import android.widget.ListView;

import com.piyush.tick.R;
import java.util.ArrayList;
import io.socket.SocketIO;
import com.piyush.tick.adapter.ImageAdapter;

/**
 * Created by rajesh on 6/7/15.
 */
public class  ProfileActivity extends Activity
{
    ImageView image_base64;
    public SocketIO socket;
    static int id = 1;
    ArrayList<String> resultArray;
    ListView list;
    BaseAdapter adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.select_profile_layout);

//        resultArray = getIntent().getStringArrayListExtra("list");
//        Log.d("Array of Profile",resultArray.toString());

        list = (ListView)findViewById(R.id.list);

        if(socket.isConnected())
        {
          adapter = new ImageAdapter(this);
            list.setAdapter(adapter);
        }


        String base = "";
        byte[] imageAsBytes = Base64.decode(base.getBytes(), Base64.DEFAULT);
        image_base64 = (ImageView)findViewById(R.id.image_converter);
        image_base64.setImageBitmap(BitmapFactory.decodeByteArray(imageAsBytes,0,imageAsBytes.length));




    }


}
